					"file":   "file",
					"folder": "folder",
				},
				Fields: map[string]string{
					"path":     "path",
					"content":  "content",
					"filename": "filename",
				},
			},
			TOML: types.FormatConfig{
				Enabled: true,
				Structure: map[string]interface{}{
					"file_section":   "file",
					"folder_section": "folder",
				},
				Fields: map[string]string{
					"path":     "path",
					"content":  "content",
					"filename": "filename",
				},
			},
			Markdown: types.FormatConfig{
				Enabled: true,
