func TestConfigGetterFunctions(t *testing.T) {
	// ä¿å­˜åŸå§‹ç¯å¢ƒå˜é‡
	originalValues := make(map[string]string)
	envKeys := []string{
		EnvDefaultFormat,
		EnvOutputDir,
		EnvFilenameTemplate,
		EnvTimestampFormat,
		EnvMaxFileSize,
		EnvMaxDepth,
