	folder := createTestFolderInfo()

	result, err := formatter.FormatFolder(folder)
	if err != nil {
		t.Fatalf("FormatFolder failed: %v", err)
	}

	// éªŒè¯ç»“æœåŒ…å«XMLå¤´
	if !strings.HasPrefix(result, xml.Header) {
		t.Error("XML result should start with XML header")
	}

