// TestParseFileSize æµ‹è¯•æ–‡ä»¶å¤§å°è§£æåŠŸèƒ½
func TestParseFileSize(t *testing.T) {
	tests := []struct {
		name     string
		sizeStr  string
		expected int64
		wantErr  bool
	}{
		{
			name:     "parse bytes",
			sizeStr:  "1024",
			expected: 1024,
			wantErr:  false,
		},
		{
			name:     "parse KB",
			sizeStr:  "1KB",
			expected: 1024,
			wantErr:  false,
		},
		{
			name:     "parse MB",
			sizeStr:  "1MB",
			expected: 1024 * 1024,
			wantErr:  false,
		},
		{
			name:     "parse GB",
			sizeStr:  "1GB",
			expected: 1024 * 1024 * 1024,
			wantErr:  false,
		},
		{
			name:     "parse with space",
			sizeStr:  "1 MB",
			expected: 1024 * 1024,
			wantErr:  false,
		},
		{
			name:     "parse with lowercase",
			sizeStr:  "1mb",
			expected: 1024 * 1024,
			wantErr:  false,
		},
		{
			name:     "parse invalid format",
			sizeStr:  "invalid",
			expected: 0,
			wantErr:  true,
		},
		{
			name:     "parse with decimal",
			sizeStr:  "1.5MB",
			expected: int64(1.5 * 1024 * 1024),
			wantErr:  false,
		},
		{
			name:     "parse unsupported unit",
			sizeStr:  "1TB",
			expected: 0,
			wantErr:  true,
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			result, err := parseFileSize(tt.sizeStr)

			if tt.wantErr {
				if err == nil {
					t.Errorf("parseFileSize() expected error but got none")
				}
			} else {
				if err != nil {
					t.Errorf("parseFileSize() unexpected error = %v", err)
				}
				if result != tt.expected {
					t.Errorf("parseFileSize() = %v, expected %v", result, tt.expected)
				}
			}
		})
	}
}

