// Package utils æä¾›é€šç”¨å·¥å…·å‡½æ•°
package utils

import (
	"crypto/md5"
	"encoding/hex"
	"fmt"
	"io"
	"os"
	"path/filepath"
	"regexp"
	"strings"
	"time"
)

// FileUtils æ–‡ä»¶å·¥å…·å‡½æ•°

// FileExists æ£€æŸ¥æ–‡ä»¶æ˜¯å¦å­˜åœ¨
func FileExists(path string) bool {
	_, err := os.Stat(path)
	return !os.IsNotExist(err)
}

// DirectoryExists æ£€æŸ¥ç›®å½•æ˜¯å¦å­˜åœ¨
func DirectoryExists(path string) bool {
	info, err := os.Stat(path)
	if os.IsNotExist(err) {
		return false
	}
	return info.IsDir()
}

// GetFileHash è·å–æ–‡ä»¶å“ˆå¸Œå€¼
func GetFileHash(path string) (string, error) {
	file, err := os.Open(path)
	if err != nil {
		return "", err
	}
	defer file.Close()

	hash := md5.New()
	if _, err := io.Copy(hash, file); err != nil {
		return "", err
	}

	return hex.EncodeToString(hash.Sum(nil)), nil
}

// GetFileSize è·å–æ–‡ä»¶å¤§å°
func GetFileSize(path string) (int64, error) {
	info, err := os.Stat(path)
	if err != nil {
		return 0, err
	}
	return info.Size(), nil
}

// GetFileModTime è·å–æ–‡ä»¶ä¿®æ”¹æ—¶é—´
func GetFileModTime(path string) (time.Time, error) {
	info, err := os.Stat(path)
	if err != nil {
		return time.Time{}, err
	}
	return info.ModTime(), nil
}

// IsTextFile æ£€æŸ¥æ˜¯å¦ä¸ºæ–‡æœ¬æ–‡ä»¶
func IsTextFile(path string) bool {
	// é¦–å…ˆæ£€æŸ¥æ–‡ä»¶æ‰©å±•å
	ext := strings.ToLower(filepath.Ext(path))
	textExtensions := []string{
		".txt", ".md", ".json", ".xml", ".yaml", ".yml", ".toml",
		".go", ".py", ".js", ".ts", ".java", ".cpp", ".c", ".h",
		".html", ".css", ".scss", ".sass", ".sql", ".sh", ".bat",
		".ps1", ".rb", ".php", ".rs", ".swift", ".kt", ".scala",
	}

	for _, textExt := range textExtensions {
		if ext == textExt {
			return true
		}
	}

	// å¦‚æœæ²¡æœ‰æ‰©å±•åï¼Œå°è¯•è¯»å–æ–‡ä»¶å†…å®¹æ¥åˆ¤æ–­
	if ext == "" {
		file, err := os.Open(path)
		if err != nil {
			return false // æ— æ³•æ‰“å¼€æ–‡ä»¶ï¼Œå‡è®¾ä¸ºäºŒè¿›åˆ¶æ–‡ä»¶
		}
		defer file.Close()

		// è¯»å–å‰512å­—èŠ‚æ¥åˆ¤æ–­æ˜¯å¦ä¸ºæ–‡æœ¬æ–‡ä»¶
		buffer := make([]byte, 512)
		n, err := file.Read(buffer)
		if err != nil && err != io.EOF {
			return false // è¯»å–é”™è¯¯ï¼Œå‡è®¾ä¸ºäºŒè¿›åˆ¶æ–‡ä»¶
		}

		// æ£€æŸ¥æ˜¯å¦åŒ…å«nullå­—èŠ‚ï¼ˆäºŒè¿›åˆ¶æ–‡ä»¶çš„æ ‡å¿—ï¼‰
		for i := 0; i < n; i++ {
			if buffer[i] == 0 {
				return false // åŒ…å«nullå­—èŠ‚ï¼Œæ˜¯äºŒè¿›åˆ¶æ–‡ä»¶
			}
		}

		// æ£€æŸ¥æ˜¯å¦åŒ…å«å¯æ‰“å°å­—ç¬¦
		printableCount := 0
		for i := 0; i < n; i++ {
			b := buffer[i]
			if b >= 32 && b <= 126 { // å¯æ‰“å°ASCIIå­—ç¬¦
				printableCount++
			} else if b == 9 || b == 10 || b == 13 { // tab, newline, carriage return
				printableCount++
			}
		}

		// å¦‚æœå¤§éƒ¨åˆ†å­—ç¬¦éƒ½æ˜¯å¯æ‰“å°çš„ï¼Œè®¤ä¸ºæ˜¯æ–‡æœ¬æ–‡ä»¶
		if n > 0 && float64(printableCount)/float64(n) > 0.8 {
			return true
		}
	}

	return false
}

// IsBinaryFile æ£€æŸ¥æ˜¯å¦ä¸ºäºŒè¿›åˆ¶æ–‡ä»¶
func IsBinaryFile(path string) bool {
	return !IsTextFile(path)
}

// StringUtils å­—ç¬¦ä¸²å·¥å…·å‡½æ•°

// TruncateString æˆªæ–­å­—ç¬¦ä¸²
func TruncateString(s string, maxLength int) string {
	if len(s) <= maxLength {
		return s
	}
	if maxLength <= 3 {
		return s[:maxLength]
	}
	return s[:maxLength-3] + "..."
}

// PadString å¡«å……å­—ç¬¦ä¸²
func PadString(s string, length int, padChar rune) string {
	if len(s) >= length {
		return s
	}
	padding := strings.Repeat(string(padChar), length-len(s))
	return s + padding
}

// PadLeft å·¦å¡«å……
func PadLeft(s string, length int, padChar rune) string {
	if len(s) >= length {
		return s
	}
	padding := strings.Repeat(string(padChar), length-len(s))
	return padding + s
}

// PadCenter å±…ä¸­å¡«å……
func PadCenter(s string, length int, padChar rune) string {
	if len(s) >= length {
		return s
	}
	totalPadding := length - len(s)
	leftPadding := totalPadding / 2
	rightPadding := totalPadding - leftPadding
	return strings.Repeat(string(padChar), leftPadding) + s + strings.Repeat(string(padChar), rightPadding)
}

// RemoveDuplicates ç§»é™¤å­—ç¬¦ä¸²åˆ‡ç‰‡ä¸­çš„é‡å¤é¡¹
func RemoveDuplicates(strings []string) []string {
	seen := make(map[string]bool)
	result := make([]string, 0, len(strings))

	for _, s := range strings {
		if !seen[s] {
			seen[s] = true
			result = append(result, s)
		}
	}

	return result
}

// SplitLines åˆ†å‰²å­—ç¬¦ä¸²ä¸ºå¤šè¡Œ
func SplitLines(s string) []string {
	return strings.Split(strings.ReplaceAll(s, "\r\n", "\n"), "\n")
}

// JoinLines è¿æ¥å¤šè¡Œä¸ºå­—ç¬¦ä¸²
func JoinLines(lines []string) string {
	return strings.Join(lines, "\n")
}

// CountLines è®¡ç®—è¡Œæ•°
func CountLines(s string) int {
	return len(SplitLines(s))
}

// PathUtils è·¯å¾„å·¥å…·å‡½æ•°

// NormalizePath è§„èŒƒåŒ–è·¯å¾„
func NormalizePath(path string) string {
	return filepath.Clean(path)
}

// GetRelativePath è·å–ç›¸å¯¹è·¯å¾„
func GetRelativePath(base, target string) (string, error) {
	return filepath.Rel(base, target)
}

// GetAbsolutePath è·å–ç»å¯¹è·¯å¾„
func GetAbsolutePath(path string) (string, error) {
	return filepath.Abs(path)
}

// IsSubPath æ£€æŸ¥æ˜¯å¦ä¸ºå­è·¯å¾„
func IsSubPath(parent, child string) bool {
	rel, err := filepath.Rel(parent, child)
	if err != nil {
		return false
	}
	// å¦‚æœç›¸å¯¹è·¯å¾„æ˜¯ "." æˆ–ç©ºå­—ç¬¦ä¸²ï¼Œè¯´æ˜æ˜¯åŒä¸€ä¸ªè·¯å¾„ï¼Œä¸ç®—å­è·¯å¾„
	if rel == "." || rel == "" {
		return false
	}
	return !strings.HasPrefix(rel, "..")
}

// GetCommonPath è·å–å…±åŒè·¯å¾„
func GetCommonPath(paths []string) string {
	if len(paths) == 0 {
		return ""
	}

	if len(paths) == 1 {
		return filepath.Dir(paths[0])
	}

	// è½¬æ¢ä¸ºç»å¯¹è·¯å¾„å¹¶æ¸…ç†
	absPaths := make([]string, 0, len(paths))
	for _, path := range paths {
		absPath, err := filepath.Abs(path)
		if err != nil {
			continue // è·³è¿‡æ— æ•ˆè·¯å¾„
		}
		absPaths = append(absPaths, filepath.Clean(absPath))
	}

	if len(absPaths) == 0 {
		return ""
	}

	// æ‰¾åˆ°æœ€çŸ­çš„è·¯å¾„
	minPath := absPaths[0]
	for _, path := range absPaths {
		if len(path) < len(minPath) {
			minPath = path
		}
	}

	// ä»æœ€çŸ­è·¯å¾„å¼€å§‹ï¼Œé€æ­¥å‘ä¸ŠæŸ¥æ‰¾å…±åŒè·¯å¾„
	for {
		common := true
		for _, path := range absPaths {
			// ä½¿ç”¨ filepath.HasPrefix æ¥å¤„ç†è·¯å¾„åˆ†éš”ç¬¦é—®é¢˜
			if !filepath.HasPrefix(path, minPath) {
				common = false
				break
			}
		}

		if common {
			return minPath
		}

		parent := filepath.Dir(minPath)
		if parent == minPath {
			break
		}
		minPath = parent
	}

	return ""
}

// RegexUtils æ­£åˆ™è¡¨è¾¾å¼å·¥å…·å‡½æ•°

// MatchPattern åŒ¹é…æ¨¡å¼
func MatchPattern(pattern, text string) (bool, error) {
	matched, err := regexp.MatchString(pattern, text)
	if err != nil {
		return false, fmt.Errorf("æ­£åˆ™è¡¨è¾¾å¼åŒ¹é…å¤±è´¥: %w", err)
	}
	return matched, nil
}

// FindMatches æŸ¥æ‰¾æ‰€æœ‰åŒ¹é…
func FindMatches(pattern, text string) ([]string, error) {
	re, err := regexp.Compile(pattern)
	if err != nil {
		return nil, fmt.Errorf("ç¼–è¯‘æ­£åˆ™è¡¨è¾¾å¼å¤±è´¥: %w", err)
	}
	return re.FindAllString(text, -1), nil
}

// ReplacePattern æ›¿æ¢æ¨¡å¼
func ReplacePattern(pattern, replacement, text string) (string, error) {
	re, err := regexp.Compile(pattern)
	if err != nil {
		return "", fmt.Errorf("ç¼–è¯‘æ­£åˆ™è¡¨è¾¾å¼å¤±è´¥: %w", err)
	}
	return re.ReplaceAllString(text, replacement), nil
}

// TimeUtils æ—¶é—´å·¥å…·å‡½æ•°

// FormatDuration æ ¼å¼åŒ–æŒç»­æ—¶é—´
func FormatDuration(d time.Duration) string {
	if d < time.Minute {
		return fmt.Sprintf("%.1fs", d.Seconds())
	}
	if d < time.Hour {
		return fmt.Sprintf("%.1fm", d.Minutes())
	}
	return fmt.Sprintf("%.1fh", d.Hours())
}

// ParseTime è§£ææ—¶é—´å­—ç¬¦ä¸²
func ParseTime(timeStr string) (time.Time, error) {
	formats := []string{
		time.RFC3339,
		"2006-01-02 15:04:05",
		"2006-01-02",
		"15:04:05",
		"2006/01/02",
	}

	for _, format := range formats {
		t, err := time.Parse(format, timeStr)
		if err == nil {
			return t, nil
		}
	}

	return time.Time{}, fmt.Errorf("æ— æ³•è§£ææ—¶é—´å­—ç¬¦ä¸²: %s", timeStr)
}

// FormatFileSize æ ¼å¼åŒ–æ–‡ä»¶å¤§å°
func FormatFileSize(bytes int64) string {
	const unit = 1024
	if bytes < unit {
		return fmt.Sprintf("%d B", bytes)
	}

	div, exp := int64(unit), 0
	for n := bytes / unit; n >= unit; n /= unit {
		div *= unit
		exp++
	}

	units := []string{"KB", "MB", "GB", "TB", "PB"}
	return fmt.Sprintf("%.1f %s", float64(bytes)/float64(div), units[exp])
}

// ValidationUtils éªŒè¯å·¥å…·å‡½æ•°

// IsValidFilename æ£€æŸ¥æ–‡ä»¶åæ˜¯å¦æœ‰æ•ˆ
func IsValidFilename(filename string) bool {
	if filename == "" {
		return false
	}

	// æ£€æŸ¥æ˜¯å¦åŒ…å«éæ³•å­—ç¬¦
	invalidChars := []string{"/", "\\", ":", "*", "?", "\"", "<", ">", "|"}
	for _, char := range invalidChars {
		if strings.Contains(filename, char) {
			return false
		}
	}

	// æ£€æŸ¥æ˜¯å¦ä»¥ç‚¹æˆ–ç©ºæ ¼å¼€å¤´/ç»“å°¾
	if strings.HasPrefix(filename, ".") || strings.HasSuffix(filename, ".") ||
	   strings.HasPrefix(filename, " ") || strings.HasSuffix(filename, " ") {
		return false
	}

	return true
}

// IsValidPath æ£€æŸ¥è·¯å¾„æ˜¯å¦æœ‰æ•ˆ
func IsValidPath(path string) bool {
	if path == "" {
		return false
	}

	// æ£€æŸ¥è·¯å¾„é•¿åº¦
	if len(path) > 260 { // Windowsè·¯å¾„é•¿åº¦é™åˆ¶
		return false
	}

	// æ£€æŸ¥æ˜¯å¦åŒ…å«ç©ºå­—ç¬¦
	if strings.Contains(path, "\x00") {
		return false
	}

	return true
}

// SafePathJoin å®‰å…¨åœ°è¿æ¥è·¯å¾„
func SafePathJoin(base, elem string) (string, error) {
	// æ£€æŸ¥è·¯å¾„éå†æ”»å‡»
	if strings.Contains(elem, "..") {
		return "", fmt.Errorf("è·¯å¾„åŒ…å«éæ³•å­—ç¬¦: %s", elem)
	}

	joined := filepath.Join(base, elem)

	// ç¡®ä¿ç»“æœä»åœ¨åŸºç¡€è·¯å¾„å†…
	if !strings.HasPrefix(filepath.Clean(joined), filepath.Clean(base)) {
		return "", fmt.Errorf("è·¯å¾„è¶…å‡ºåŸºç¡€ç›®å½•èŒƒå›´")
	}

	return joined, nil
}

// ReadFileContent è¯»å–æ–‡ä»¶å†…å®¹ï¼ˆå¸¦å¤§å°é™åˆ¶ï¼‰
func ReadFileContent(path string, maxSize int64) (string, bool, error) {
	// è·å–æ–‡ä»¶ä¿¡æ¯
	info, err := os.Stat(path)
	if err != nil {
		return "", false, err
	}

	// æ£€æŸ¥æ–‡ä»¶å¤§å°
	if maxSize > 0 && info.Size() > maxSize {
		return "", false, fmt.Errorf("æ–‡ä»¶å¤§å°è¶…è¿‡é™åˆ¶: %d > %d", info.Size(), maxSize)
	}

	// è¯»å–æ–‡ä»¶å†…å®¹
	content, err := os.ReadFile(path)
	if err != nil {
		return "", false, fmt.Errorf("è¯»å–æ–‡ä»¶å¤±è´¥: %w", err)
	}

	// æ£€æµ‹æ˜¯å¦ä¸ºäºŒè¿›åˆ¶æ–‡ä»¶
	isBinary := !IsTextFile(path)

	return string(content), isBinary, nil
}

// ColorUtils é¢œè‰²å·¥å…·å‡½æ•°

// ColorCode é¢œè‰²ä»£ç 
type ColorCode string

const (
	ColorReset  ColorCode = "\033[0m"
	ColorRed    ColorCode = "\033[31m"
	ColorGreen  ColorCode = "\033[32m"
	ColorYellow ColorCode = "\033[33m"
	ColorBlue   ColorCode = "\033[34m"
	ColorPurple ColorCode = "\033[35m"
	ColorCyan   ColorCode = "\033[36m"
	ColorWhite  ColorCode = "\033[37m"
)

// Colorize ç»™æ–‡æœ¬æ·»åŠ é¢œè‰²
func Colorize(text string, color ColorCode) string {
	return string(color) + text + string(ColorReset)
}

// ErrorColor é”™è¯¯é¢œè‰²
func ErrorColor(text string) string {
	return Colorize(text, ColorRed)
}

// SuccessColor æˆåŠŸé¢œè‰²
func SuccessColor(text string) string {
	return Colorize(text, ColorGreen)
}

// WarningColor è­¦å‘Šé¢œè‰²
func WarningColor(text string) string {
	return Colorize(text, ColorYellow)
}

// InfoColor ä¿¡æ¯é¢œè‰²
func InfoColor(text string) string {
	return Colorize(text, ColorBlue)
}

