// TestPathUtils è·¯å¾„å·¥å…·å‡½æ•°æµ‹è¯•
func TestNormalizePath(t *testing.T) {
	testCases := []struct {
		input    string
		expected string
	}{
		{"path/to/file", filepath.Join("path", "to", "file")},
		{"path/../file", "file"},
		{"./path/to/file", filepath.Join("path", "to", "file")},
		{"/absolute/path", filepath.Join("/", "absolute", "path")},
	}

	for _, tc := range testCases {
		result := NormalizePath(tc.input)
		if result != tc.expected {
			t.Errorf("NormalizePath(%q) = %q, æœŸæœ› %q", tc.input, result, tc.expected)
		}
	}
}

func TestGetRelativePath(t *testing.T) {
	// åˆ›å»ºä¸´æ—¶ç›®å½•ç»“æ„
	tempDir := t.TempDir()
	subDir := filepath.Join(tempDir, "subdir")
	if err := os.MkdirAll(subDir, 0755); err != nil {
		t.Fatalf("åˆ›å»ºæµ‹è¯•ç›®å½•å¤±è´¥: %v", err)
	}

	testFile := filepath.Join(subDir, "test.txt")
	if err := os.WriteFile(testFile, []byte("test"), 0644); err != nil {
		t.Fatalf("åˆ›å»ºæµ‹è¯•æ–‡ä»¶å¤±è´¥: %v", err)
	}

	// æµ‹è¯•è·å–ç›¸å¯¹è·¯å¾„
	relPath, err := GetRelativePath(tempDir, testFile)
	if err != nil {
		t.Errorf("GetRelativePath è¿”å›é”™è¯¯: %v", err)
	}
	expected := filepath.Join("subdir", "test.txt")
	if filepath.ToSlash(relPath) != filepath.ToSlash(expected) {
		t.Errorf("GetRelativePath è¿”å›çš„è·¯å¾„ä¸æ­£ç¡®: æœŸæœ› %q, å®é™… %q", expected, relPath)
	}

	// æµ‹è¯•æ— æ•ˆè·¯å¾„
	_, err = GetRelativePath("/nonexistent", testFile)
	if err == nil {
		t.Error("GetRelativePath åº”è¯¥å¯¹æ— æ•ˆè·¯å¾„è¿”å›é”™è¯¯")
	}
}

func TestGetAbsolutePath(t *testing.T) {
	// æµ‹è¯•ç›¸å¯¹è·¯å¾„
	relPath := "utils.go"
	absPath, err := GetAbsolutePath(relPath)
	if err != nil {
		t.Errorf("GetAbsolutePath è¿”å›é”™è¯¯: %v", err)
	}
	if !filepath.IsAbs(absPath) {
		t.Error("GetAbsolutePath åº”è¯¥è¿”å›ç»å¯¹è·¯å¾„")
	}

	// æµ‹è¯•å·²ç»å­˜åœ¨çš„ç»å¯¹è·¯å¾„
	if _, err := GetAbsolutePath(absPath); err != nil {
		t.Errorf("GetAbsolutePath å¯¹ç»å¯¹è·¯å¾„è¿”å›é”™è¯¯: %v", err)
	}
}

