		{"[", "hello", false, true}, // æ— æ•ˆçš„æ­£åˆ™è¡¨è¾¾å¼
	}

	for _, tc := range testCases {
		result, err := MatchPattern(tc.pattern, tc.text)
		if tc.hasError {
			if err == nil {
				t.Errorf("MatchPattern(%q, %q) åº”è¯¥è¿”å›é”™è¯¯", tc.pattern, tc.text)
			}
		} else {
			if err != nil {
				t.Errorf("MatchPattern(%q, %q) è¿”å›æ„å¤–é”™è¯¯: %v", tc.pattern, tc.text, err)
			}
			if result != tc.expected {
				t.Errorf("MatchPattern(%q, %q) = %v, æœŸæœ› %v", tc.pattern, tc.text, result, tc.expected)
			}
		}
	}
}

func TestFindMatches(t *testing.T) {
	testCases := []struct {
		pattern  string
		text     string
		expected []string
		hasError bool
	}{
		{"l", "hello world", []string{"l", "l", "l"}, false},
		{"o", "hello world", []string{"o", "o"}, false},
		{"xyz", "hello world", []string{}, false},
		{"[", "hello", nil, true}, // æ— æ•ˆçš„æ­£åˆ™è¡¨è¾¾å¼
	}

	for _, tc := range testCases {
		result, err := FindMatches(tc.pattern, tc.text)
		if tc.hasError {
			if err == nil {
				t.Errorf("FindMatches(%q, %q) åº”è¯¥è¿”å›é”™è¯¯", tc.pattern, tc.text)
			}
		} else {
			if err != nil {
				t.Errorf("FindMatches(%q, %q) è¿”å›æ„å¤–é”™è¯¯: %v", tc.pattern, tc.text, err)
			}
			if len(result) != len(tc.expected) {
				t.Errorf("FindMatches(%q, %q) è¿”å›çš„åŒ¹é…æ•°é‡ä¸æ­£ç¡®: æœŸæœ› %d, å®é™… %d", tc.pattern, tc.text, len(tc.expected), len(result))
				continue
			}
			for i := range result {
				if result[i] != tc.expected[i] {
					t.Errorf("FindMatches(%q, %q) è¿”å›çš„ç»“æœä¸åŒ¹é…: æœŸæœ› %v, å®é™… %v", tc.pattern, tc.text, tc.expected, result)
					break
				}
			}
		}
	}
}

