func TestParseTime(t *testing.T) {
	testCases := []struct {
		timeStr  string
		hasError bool
	}{
		{"2023-01-01T12:00:00Z", false},     // RFC3339
		{"2023-01-01 12:00:00", false},     // 2006-01-02 15:04:05
		{"2023-01-01", false},              // 2006-01-02
		{"12:00:00", false},                // 15:04:05
		{"2023/01/01", false},              // 2006/01/02
		{"invalid", true},                  // æ— æ•ˆæ ¼å¼
		{"2023-13-01", true},               // æ— æ•ˆæ—¥æœŸ
	}

	for _, tc := range testCases {
		result, err := ParseTime(tc.timeStr)
		if tc.hasError {
			if err == nil {
				t.Errorf("ParseTime(%q) åº”è¯¥è¿”å›é”™è¯¯", tc.timeStr)
			}
		} else {
			if err != nil {
				t.Errorf("ParseTime(%q) è¿”å›æ„å¤–é”™è¯¯: %v", tc.timeStr, err)
			}
			if result.IsZero() {
				t.Errorf("ParseTime(%q) è¿”å›é›¶æ—¶é—´", tc.timeStr)
			}
		}
	}
}

