func TestSafePathJoin(t *testing.T) {
	testCases := []struct {
		base     string
		elem     string
		expected string
		hasError bool
	}{
		{"/base", "file.txt", filepath.Join("/base", "file.txt"), false},
		{"/base", "subdir/file.txt", filepath.Join("/base", "subdir", "file.txt"), false},
		{"/base", "../file.txt", "", true}, // è·¯å¾„éå†æ”»å‡»
		{"/base", "subdir/../file.txt", "", true}, // è·¯å¾„éå†æ”»å‡»
		{"/base", "", filepath.Join("/base", ""), false},
	}

	for _, tc := range testCases {
		result, err := SafePathJoin(tc.base, tc.elem)
		if tc.hasError {
			if err == nil {
				t.Errorf("SafePathJoin(%q, %q) åº”è¯¥è¿”å›é”™è¯¯", tc.base, tc.elem)
			}
		} else {
			if err != nil {
				t.Errorf("SafePathJoin(%q, %q) è¿”å›æ„å¤–é”™è¯¯: %v", tc.base, tc.elem, err)
			}
			if result != tc.expected {
				t.Errorf("SafePathJoin(%q, %q) = %q, æœŸæœ› %q", tc.base, tc.elem, result, tc.expected)
			}
		}
	}
}

