package main

import (
	"fmt"
	"log"

	"code-context-generator/internal/config"
	"code-context-generator/internal/env"
)

func main() {
	// é¦–å…ˆåŠ è½½.envæ–‡ä»¶ï¼ˆå¦‚æœå­˜åœ¨ï¼‰
	if err := env.LoadEnv(""); err != nil {
		log.Printf("è­¦å‘Š: åŠ è½½.envæ–‡ä»¶å¤±è´¥: %v", err)
	}

	// åˆ›å»ºé…ç½®ç®¡ç†å™¨
	cm := config.NewManager()

	// è·å–é»˜è®¤é…ç½®
	fmt.Printf("é»˜è®¤é…ç½®: %+v\n", cm.Get())

	// ä¿å­˜é…ç½®ä¸ºYAMLæ ¼å¼
	if err := cm.Save("config.yaml", "yaml"); err != nil {
		log.Fatalf("ä¿å­˜é…ç½®å¤±è´¥: %v", err)
	}
	fmt.Println("é…ç½®å·²ä¿å­˜ä¸ºYAMLæ ¼å¼")
}

