// AppError åº”ç”¨é”™è¯¯ç±»å‹
type AppError struct {
	Type    ErrorType
	Message string
	Cause   error
	Context map[string]interface{}
}

// ErrorType é”™è¯¯ç±»å‹æšä¸¾
type ErrorType int

const (
	ErrConfig ErrorType = iota
	ErrFileSystem
	ErrFormat
	ErrValidation
	ErrPermission
	ErrNetwork
	ErrUnknown
)

// String è¿”å›é”™è¯¯ç±»å‹çš„å­—ç¬¦ä¸²è¡¨ç¤º
func (et ErrorType) String() string {
	switch et {
	case ErrConfig:
		return "ConfigError"
	case ErrFileSystem:
		return "FileSystemError"
	case ErrFormat:
		return "FormatError"
	case ErrValidation:
		return "ValidationError"
	case ErrPermission:
		return "PermissionError"
	case ErrNetwork:
		return "NetworkError"
	default:
		return "UnknownError"
	}
}

// Error å®ç°erroræ¥å£
func (ae *AppError) Error() string {
	if ae.Cause != nil {
		return fmt.Sprintf("%s: %s (caused by: %v)", ae.Type, ae.Message, ae.Cause)
	}
	return fmt.Sprintf("%s: %s", ae.Type, ae.Message)
}

// Unwrap è¿”å›åº•å±‚é”™è¯¯
func (ae *AppError) Unwrap() error {
	return ae.Cause
}

