type SelectorOptions struct {
	MaxDepth        int
	IncludePatterns []string
	ExcludePatterns []string
	ShowHidden      bool
	SortBy          string
}

// FileItem æ–‡ä»¶é¡¹
type FileItem struct {
	Path     string
	Name     string
	Size     int64
	ModTime  time.Time
	IsDir    bool
	IsHidden bool
	Icon     string
	Type     string
	Selected bool
}

