		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			pm := NewPatternMatcher(tt.patterns)
			result := pm.MatchAny(tt.path)

			if result != tt.expected {
				t.Errorf("MatchAny() = %v, expected %v", result, tt.expected)
			}
		})
	}
}

